 dispatch({ type: 'FETCH_SUCCESS', payload: userData });

 const userPosts = await api.getUserPosts(id);
 dispatch({ type: 'FETCH_POSTS_SUCCESS', payload: userPosts });
 } catch (error) {
 dispatch({ type: 'FETCH_ERROR', payload: error.message });
 }
 };

