restAPI.code:
const http = require('http');

// Simple in-memory database
let todos = [
  { id: 1, text: 'Learn Node.js', completed: false },
  { id: 2, text: 'Build an API', completed: false }
];

// Helper function to parse JSON body
function parseBody(req) {
  return new Promise((resolve, reject) => {
    let body = '';

    req.on('data', (chunk) => {
      body += chunk.toString();
    });

    req.on('end', () => {
      try {
        resolve(body ? JSON.parse(body) : {});
      } catch (error) {
        reject(error);
      }
    });
  });
}

// Create server
const server = http.createServer(async (req, res) => {
  // Set CORS headers
  res.setHeader('Access-Control-Allow-Origin', '*');
  res.setHeader('Access-Control-Allow-Methods', 'GET, POST, PUT, DELETE');
  res.setHeader('Access-Control-Allow-Headers', 'Content-Type');

  // Handle OPTIONS request (for CORS preflight)
  if (req.method === 'OPTIONS') {
    res.statusCode = 204; // No Content
    return res.end();
  }

  // Extract URL and method
  const url = req.url;
  const method = req.method;

  // Set content type for all responses
  res.setHeader('Content-Type', 'application/json');

  // Routes handling
  try {
    // GET /todos - Get all todos
    if (url === '/todos' && method === 'GET') {
      res.statusCode = 200;
      return res.end(JSON.stringify(todos));
    }

    // GET /todos/:id - Get a specific todo
    if (url.match(/^\/todos\/\d+$/) && method === 'GET') {
      const id = parseInt(url.split('/')[2]);
      const todo = todos.find(t => t.id === id);

      if (!todo) {
        res.statusCode = 404;
        return res.end(JSON.stringify({ error: 'Todo not found' }));
      }

      res.statusCode = 200;
      return res.end(JSON.stringify(todo));
    }

    // POST /todos - Create a new todo
    if (url === '/todos' && method === 'POST') {
      const body = await parseBody(req);

      if (!body.text) {
        res.statusCode = 400;
        return res.end(JSON.stringify({ error: 'Text is required' }));
      }

      const newTodo = {
        id: todos.length > 0 ? Math.max(...todos.map(t => t.id)) + 1 : 1,
        text: body.text,
        completed: body.completed || false
      };

      todos.push(newTodo);

      res.statusCode = 201;
      return res.end(JSON.stringify(newTodo));
    }

    // PUT /todos/:id - Update a todo
    if (url.match(/^\/todos\/\d+$/) && method === 'PUT') {
      const id = parseInt(url.split('/')[2]);
      const todoIndex = todos.findIndex(t => t.id === id);

      if (todoIndex === -1) {
        res.statusCode = 404;
        return res.end(JSON.stringify({ error: 'Todo not found' }));
      }

      const body = await parseBody(req);

      todos[todoIndex] = {
        ...todos[todoIndex],
        ...(body.text !== undefined && { text: body.text }),
        ...(body.completed !== undefined && { completed: body.completed })
      };

      res.statusCode = 200;
      return res.end(JSON.stringify(todos[todoIndex]));
    }

    // DELETE /todos/:id - Delete a todo
    if (url.match(/^\/todos\/\d+$/) && method === 'DELETE') {
      const id = parseInt(url.split('/')[2]);
      const todoIndex = todos.findIndex(t => t.id === id);

      if (todoIndex === -1) {
        res.statusCode = 404;
        return res.end(JSON.stringify({ error: 'Todo not found' }));
      }

      const deletedTodo = todos[todoIndex];
      todos = todos.filter(t => t.id !== id);

      res.statusCode = 200;
      return res.end(JSON.stringify(deletedTodo));
    }

    // If no route matches
    res.statusCode = 404;
    return res.end(JSON.stringify({ error: 'Not found' }));
  }

