const fs = require('fs');
const path = require('path');

// Log file paths
const logFile = 'app.log';
const rotatedLogFile = (index) => `app.log.${index}`;

// Function to rotate logs
async function rotateLog() {
  try {
    // Check if log file exists
    try {
      await fs.promises.access(logFile);
    } catch {
      // Log file doesn't exist, no need to rotate
      return;
    }

    // Get current log file size
    const stats = await fs.promises.stat(logFile);
    const fileSizeInMB = stats.size / (1024 * 1024);

    // Rotate if file is larger than 10MB
    if (fileSizeInMB >= 10) {
      // Find the maximum rotation index
      const dir = path.dirname(logFile);
      const files = await fs.promises.readdir(dir);

      let maxIndex = 0;
      const pattern = new RegExp(`^${path.basename(logFile)}\.\d+$`);

      for (const file of files) {
        if (pattern.test(file)) {
          const index = parseInt(file.split('.').pop());
          maxIndex = Math.max(maxIndex, index);
        }
      }

      // Rename the current log file
      await fs.promises.rename(logFile, rotatedLogFile(maxIndex + 1));

      console.log(`Log rotated: ${logFile} -> ${rotatedLogFile(maxIndex + 1)}`);
    }
  } catch (err) {
    console.error('Error rotating log:', err);
  }
}

// Function to write to log
async function writeLog(message) {
  const logEntry = `[${new Date().toISOString()}] ${message}\n`;

  try {
    await fs.promises.appendFile(logFile, logEntry);

    // Check if rotation is needed after write
    await rotateLog();
  } catch (err) {
    console.error('Error writing to log:', err);
  }
}

// Example usage
(async () => {
  for (let i = 0; i < 1000; i++) {
    await writeLog(`Test log entry ${i} with some data to increase file size...`);
  }
})();

