const fs = require('fs');
const http = require('http');
const path = require('path');

// Create upload directory if it doesn't exist
const uploadDir = path.join(__dirname, 'uploads');
if (!fs.existsSync(uploadDir)) {
  fs.mkdirSync(uploadDir, { recursive: true });
}

// Create HTTP server to handle file uploads
const server = http.createServer((req, res) => {
  if (req.method === 'POST' && req.url === '/upload') {
    // Handle file upload
    const fileName = `upload-${Date.now()}${path.extname(req.headers['file-name'] || '') || '.bin'}`;
    const filePath = path.join(uploadDir, fileName);

    // Create a write stream
    const fileWriteStream = fs.createWriteStream(filePath);

    // Handle file write success
    fileWriteStream.on('finish', () => {
      res.writeHead(200, { 'Content-Type': 'application/json' });
      res.end(JSON.stringify({
        success: true,
        fileName: fileName,
        path: filePath
      }));
    });

    // Handle file write error
    fileWriteStream.on('error', (err) => {
      console.error('Error writing file:', err);
      res.writeHead(500, { 'Content-Type': 'application/json' });
      res.end(JSON.stringify({ success: false, error: 'Failed to save file' }));
    });

    // Pipe request data to file
    req.pipe(fileWriteStream);

    // Handle request errors
    req.on('error', (err) => {
      console.error('Error receiving upload:', err);
      fileWriteStream.destroy();
      fs.unlink(filePath, () => {});
    });
  } else {
    // Serve a simple HTML form for testing uploads
    if (req.url === '/' && req.method === 'GET') {
      res.writeHead(200, { 'Content-Type': 'text/html' });
      res.end(`
        <!DOCTYPE html>
        <html>
        <head>
          <title>File Upload</title>
        </head>
        <body>
          <h1>Upload a File</h1>
          <form id="uploadForm" enctype="multipart/form-data">
            <input type="file" id="fileInput" name="file" />
            <button type="submit">Upload</button>
          </form>
          <div id="result"></div>

          <script>
            document.getElementById('uploadForm').addEventListener('submit', async (e) => {
              e.preventDefault();
              const fileInput = document.getElementById('fileInput');
              const file = fileInput.files[0];
              if (!file) return alert('Please select a file');

              const result = document.getElementById('result');
              result.textContent = 'Uploading...';

              try {
                const response = await fetch('/upload', {
                  method: 'POST',
                  headers: {
                    'File-Name': file.name
                  },
                  body: file
                });

                const data = await response.json();
                result.textContent = data.success ?
                  `File uploaded: ${data.fileName}` :
                  `Upload failed: ${data.error}`;
              } catch (err) {
                result.textContent = 'Upload failed: ' + err.message;
              }
            });
          </script>
        </body>
        </html>
      `);
    } else {
      res.writeHead(404, { 'Content-Type': 'text/plain' });
      res.end('Not Found');
    }
  }
});

