const http = require('http');
const { URL } = require('url');

// Create HTTP server with URL-based routing
const server = http.createServer((req, res) => {
  // Parse the URL (using current host as base)
  const baseURL = `http://${req.headers.host}`;
  const parsedUrl = new URL(req.url, baseURL);

  // Get pathname and search params
  const pathname = parsedUrl.pathname;
  const params = parsedUrl.searchParams;

  // Set response header
  res.setHeader('Content-Type', 'text/html');

  // Simple routing based on pathname
  if (pathname === '/') {
    res.writeHead(200);
    res.end('<h1>Home Page</h1>');
  } 
  else if (pathname === '/about') {
    res.writeHead(200);
    res.end('<h1>About Page</h1>');
  } 
  else if (pathname === '/users') {
    // Get user ID from query parameters
    const userId = params.get('id');

    if (userId) {
      res.writeHead(200);
      res.end(`<h1>User Details</h1><p>Showing details for user ID: ${userId}</p>`);
    } else {
      res.writeHead(200);
      res.end('<h1>Users List</h1><p>No specific user selected.</p>');
    }
  } 
  else if (pathname.startsWith('/products/')) {
    // Extract product ID from path
    const productId = pathname.split('/')[2];

    res.writeHead(200);
    res.end(`<h1>Product Details</h1><p>Showing details for product ID: ${productId}</p>`);
  } 
  else {
    res.writeHead(404);
    res.end('<h1>404 Not Found</h1>');
  }
});

server.listen(3000, () => {
  console.log('Server running at http://localhost:3000/');
});

urlValidation.code:
const { URL } = require('url');

/**
 * Validates a URL and returns a normalized version if valid
 * @param {string} urlString - The URL to validate
 * @returns {string|null} - Normalized URL or null if invalid
 */
function validateAndNormalizeURL(urlString) {
  try {
    // Prepend a protocol if missing
    if (!urlString.match(/^[a-zA-Z][a-zA-Z\d+-.]*:/))
      urlString = `https://${urlString}`;

    // Try to create URL object (will throw if invalid)
    const myURL = new URL(urlString);

    // Allow only http and https protocols
    if (myURL.protocol !== 'http:' && myURL.protocol !== 'https:') {
      return null;
    }

    // Normalize the URL
    return myURL.href;
  } catch (err) {
    // Invalid URL
    return null;
  }
}

// Test the function
const testUrls = [
  'example.com',
  'https://example.com/path',
  'http://user:pass@example.com:8080/path?query=string#hash',
  'ftp://example.com',  // Should fail (not http/https)
  'not a url',          // Should fail (invalid)
  'https://example.com/a/../b'  // Should normalize path
];

for (const url of testUrls) {
  const result = validateAndNormalizeURL(url);
  console.log(`Original: ${url}`);
  console.log(`Result: ${result || 'Invalid URL'}\n`);
}

