const fs = require('fs');
const http = require('http');

http.createServer((req, res) => {
  if (req.method === 'POST') {
    const writeStream = fs.createWriteStream('large-file.dat');

    req.pipe(writeStream);

    req.on('end', () => {
      res.end('Upload complete');
    });

    writeStream.on('error', (err) => {
      console.error(err);
      res.statusCode = 500;
      res.end('Error saving file');
    });
  }
}).listen(3000);

