Java Operators.code:
public class OperatorsExample {
    public static void main(String[] args) {
        // Arithmetic operators
        int a = 10;
        int b = 5;
        System.out.println("a + b = " + (a + b));
        System.out.println("a - b = " + (a - b));
        System.out.println("a * b = " + (a * b));
        System.out.println("a / b = " + (a / b));
        System.out.println("a % b = " + (a % b));

        // Increment and decrement
        int c = 5;
        System.out.println("c++ = " + (c++));  // Post-increment: returns value then increments
        System.out.println("c = " + c);        // c is now 6
        System.out.println("++c = " + (++c));  // Pre-increment: increments then returns value

        // Assignment operators
        int d = 10;
        d += 5;  // equivalent to d = d + 5
        System.out.println("d after d += 5: " + d);

        // Comparison operators
        int x = 10;
        int y = 15;
        System.out.println("x == y: " + (x == y));
        System.out.println("x != y: " + (x != y));
        System.out.println("x > y: " + (x > y));
        System.out.println("x < y: " + (x < y));

        // Logical operators
        boolean isAdult = true;
        boolean isEmployed = false;
        System.out.println("isAdult AND isEmployed: " + (isAdult && isEmployed));
        System.out.println("isAdult OR isEmployed: " + (isAdult || isEmployed));
        System.out.println("NOT isAdult: " + (!isAdult));

        // Bitwise operators
        int num1 = 5;  // binary: 0101
        int num2 = 3;  // binary: 0011
        System.out.println("num1 & num2: " + (num1 & num2));  // 0001 = 1
        System.out.println("num1 | num2: " + (num1 | num2));  // 0111 = 7
        System.out.println("num1 ^ num2: " + (num1 ^ num2));  // 0110 = 6
        System.out.println("~num1: " + (~num1));              // inverts all bits

        // Shift operators
        System.out.println("num1 << 1: " + (num1 << 1));  // 0101 to 1010 = 10
        System.out.println("num1 >> 1: " + (num1 >> 1));  // 0101 to 0010 = 2

        // Ternary operator
        int age = 20;
        String status = (age >= 18) ? "Adult" : "Minor";
        System.out.println("Status: " + status);
    }
}

