import java.util.Scanner;
import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.io.IOException;
import java.io.Console;

public class UserInputOutputExample {
    public static void main(String[] args) {
        // Command line arguments
        if (args.length > 0) {
            System.out.println("Command line arguments:");
            for (int i = 0; i < args.length; i++) {
                System.out.println("Arg " + i + ": " + args[i]);
            }
        }

        // Using Scanner for input
        Scanner scanner = new Scanner(System.in);

        System.out.print("Enter your name: ");
        String name = scanner.nextLine();

        System.out.print("Enter your age: ");
        int age = scanner.nextInt();

        // Consume the leftover newline
        scanner.nextLine();

        System.out.print("Enter your email: ");
        String email = scanner.nextLine();

        // Using different output methods
        System.out.println("User Information:");
        System.out.print("Name: " + name + ", ");
        System.out.print("Age: " + age + ", ");
        System.out.println("Email: " + email);

        // Formatted output with printf
        System.out.printf("Formatted output: Name=%s, Age=%d, Email=%s%n", name, age, email);

        // Using BufferedReader for input
        try {
            BufferedReader reader = new BufferedReader(new InputStreamReader(System.in));
            System.out.print("Enter your favorite color (using BufferedReader): ");
            String color = reader.readLine();
            System.out.println("Your favorite color is: " + color);
        } catch (IOException e) {
            System.out.println("An error occurred while reading input");
            e.printStackTrace();
        }

        // Using Console for password input (works only in console, not in IDE)
        Console console = System.console();
        if (console != null) {
            char[] passwordArray = console.readPassword("Enter your password: ");
            // Convert to string for demonstration (in real apps, avoid storing as String)
            String password = new String(passwordArray);
            System.out.println("Password length: " + password.length());
            // Clear the password from memory
            java.util.Arrays.fill(passwordArray, ' ');
        } else {
            System.out.println("Console is not available");
        }

        scanner.close();
    }
}

