import java.util.Scanner;

public class SwitchCaseExample {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        // Basic switch statement with int
        System.out.print("Enter a day number (1-7): ");
        int day = scanner.nextInt();

        switch (day) {
            case 1:
                System.out.println("Monday");
                break;
            case 2:
                System.out.println("Tuesday");
                break;
            case 3:
                System.out.println("Wednesday");
                break;
            case 4:
                System.out.println("Thursday");
                break;
            case 5:
                System.out.println("Friday");
                break;
            case 6:
                System.out.println("Saturday");
                break;
            case 7:
                System.out.println("Sunday");
                break;
            default:
                System.out.println("Invalid day number");
        }

        // Switch with char
        System.out.print("\nEnter a grade (A, B, C, D, or F): ");
        char grade = scanner.next().charAt(0);
        grade = Character.toUpperCase(grade);

        switch (grade) {
            case 'A':
                System.out.println("Excellent!");
                break;
            case 'B':
                System.out.println("Good job!");
                break;
            case 'C':
                System.out.println("Satisfactory");
                break;
            case 'D':
                System.out.println("Needs improvement");
                break;
            case 'F':
                System.out.println("Failed");
                break;
            default:
                System.out.println("Invalid grade");
        }

        // Switch with String (Java 7+)
        scanner.nextLine();  // Consume remaining newline
        System.out.print("\nEnter a month name: ");
        String month = scanner.nextLine().toLowerCase();

        switch (month) {
            case "january":
            case "march":
            case "may":
            case "july":
            case "august":
            case "october":
            case "december":
                System.out.println("31 days");
                break;
            case "april":
            case "june":
            case "september":
            case "november":
                System.out.println("30 days");
                break;
            case "february":
                System.out.println("28 or 29 days");
                break;
            default:
                System.out.println("Invalid month name");
        }

        // Enhanced switch (Java 14+)
        System.out.print("\nEnter a number (1-3): ");
        int choice = scanner.nextInt();

        // Using enhanced switch with arrow syntax
        switch (choice) {
            case 1 -> System.out.println("You chose Option 1");
            case 2 -> System.out.println("You chose Option 2");
            case 3 -> System.out.println("You chose Option 3");
            default -> System.out.println("Invalid choice");
        }

        // Switch expression with return value (Java 14+)
        System.out.print("\nEnter a number (1-4): ");
        int num = scanner.nextInt();

        String result = switch (num) {
            case 1 -> "One";
            case 2 -> "Two";
            case 3 -> "Three";
            case 4 -> "Four";
            default -> "Unknown";
        };

        System.out.println("Number in words: " + result);

        scanner.close();
    }
}

