public class ForLoopExample {
    public static void main(String[] args) {
        // Basic for loop
        System.out.println("Counting from 1 to 5:");
        for (int i = 1; i <= 5; i++) {
            System.out.println("Count: " + i);
        }

        // For loop with different initialization and update
        System.out.println("\nCounting down from 10 to 1:");
        for (int i = 10; i >= 1; i--) {
            System.out.println("Count: " + i);
        }

        // For loop with multiple variables
        System.out.println("\nUsing multiple variables in for loop:");
        for (int i = 1, j = 10; i <= 5; i++, j -= 2) {
            System.out.println("i = " + i + ", j = " + j);
        }

        // For loop for array iteration
        int[] numbers = {10, 20, 30, 40, 50};
        System.out.println("\nArray elements:");
        for (int i = 0; i < numbers.length; i++) {
            System.out.println("Element at index " + i + ": " + numbers[i]);
        }

        // Enhanced for loop (for-each) - introduced in Java 5
        System.out.println("\nUsing enhanced for loop:");
        for (int number : numbers) {
            System.out.println("Element: " + number);
        }

        // For loop with complex logic
        System.out.println("\nPrinting even numbers from 1 to 10:");
        for (int i = 1; i <= 10; i++) {
            if (i % 2 == 0) {
                System.out.println(i);
            }
        }

        // For loop for string iteration
        String name = "Java";
        System.out.println("\nCharacters in '" + name + "':");
        for (int i = 0; i < name.length(); i++) {
            System.out.println("Character at position " + i + ": " + name.charAt(i));
        }
    }
}

