import java.util.Scanner;

public class WhileLoopExample {
    public static void main(String[] args) {
        // Basic while loop
        int count = 1;
        System.out.println("Counting from 1 to 5 using while loop:");
        while (count <= 5) {
            System.out.println("Count: " + count);
            count++;
        }

        // While loop for user input validation
        Scanner scanner = new Scanner(System.in);
        System.out.println("\nEnter a positive number:");
        int number = scanner.nextInt();

        while (number <= 0) {
            System.out.println("That's not a positive number. Try again:");
            number = scanner.nextInt();
        }

        System.out.println("Thank you! You entered: " + number);

        // While loop with complex condition
        int sum = 0;
        int i = 1;

        System.out.println("\nSum of numbers from 1 to 10:");
        while (i <= 10) {
            sum += i;
            i++;
        }
        System.out.println("Sum: " + sum);

        // Random number guessing game using while loop
        int targetNumber = (int) (Math.random() * 10) + 1; // Random number between 1-10
        int attempts = 0;
        boolean guessed = false;

        System.out.println("\nI'm thinking of a number between 1 and 10. Try to guess it!");

        while (!guessed) {
            System.out.print("Enter your guess: ");
            int guess = scanner.nextInt();
            attempts++;

            if (guess == targetNumber) {
                guessed = true;
                System.out.println("Congratulations! You guessed the number in " + attempts + " attempts.");
            } else if (guess < targetNumber) {
                System.out.println("Too low! Try again.");
            } else {
                System.out.println("Too high! Try again.");
            }
        }

        scanner.close();
    }
}

