    public static void main(String[] args) {
        // Basic do-while loop
        int count = 1;
        System.out.println("Counting from 1 to 5 using do-while loop:");
        do {
            System.out.println("Count: " + count);
            count++;
        } while (count <= 5);

        // do-while loop executing at least once
        int x = 10;
        System.out.println("\nExample of do-while executing once even when condition is false:");
        do {
            System.out.println("Value of x: " + x);
            x++;
        } while (x < 10); // Condition is already false, but code executed once

        // Menu-driven program using do-while
        Scanner scanner = new Scanner(System.in);
        int choice;

        do {
            System.out.println("\nMenu:");
            System.out.println("1. Say Hello");
            System.out.println("2. Say Goodbye");
            System.out.println("3. Tell a Joke");
            System.out.println("4. Exit");
            System.out.print("Enter your choice (1-4): ");

            choice = scanner.nextInt();

            switch (choice) {
                case 1:
                    System.out.println("Hello there!");
                    break;
                case 2:
                    System.out.println("Goodbye!");
                    break;
                case 3:
                    System.out.println("Why don't scientists trust atoms? Because they make up everything!");
                    break;
                case 4:
                    System.out.println("Exiting program...");
                    break;
                default:
                    System.out.println("Invalid choice. Please try again.");
            }
        } while (choice != 4);

        // Input validation with do-while
        int number;
        do {
            System.out.print("\nEnter a number between 1 and 10: ");
            number = scanner.nextInt();

            if (number < 1 || number > 10) {
                System.out.println("Invalid input! The number must be between 1 and 10.");
            }
        } while (number < 1 || number > 10);

        System.out.println("Thank you! You entered: " + number);

        scanner.close();
    }
}

