public class NestedLoopsExample {
    public static void main(String[] args) {
        // Multiplication table using nested for loops
        System.out.println("Multiplication Table (1-5):");
        for (int i = 1; i <= 5; i++) {
            for (int j = 1; j <= 5; j++) {
                System.out.print(i + "x" + j + "=" + (i * j) + "\t");
            }
            System.out.println(); // New line after each row
        }

        // Printing patterns using nested loops
        System.out.println("\nPattern 1:");
        for (int i = 1; i <= 5; i++) {
            for (int j = 1; j <= i; j++) {
                System.out.print("* ");
            }
            System.out.println();
        }

        System.out.println("\nPattern 2:");
        for (int i = 5; i >= 1; i--) {
            for (int j = 1; j <= i; j++) {
                System.out.print("* ");
            }
            System.out.println();
        }

        System.out.println("\nPattern 3 (Number triangle):");
        for (int i = 1; i <= 5; i++) {
            for (int j = 1; j <= i; j++) {
                System.out.print(j + " ");
            }
            System.out.println();
        }

        // Nested loops with arrays
        int[][] matrix = {
            {1, 2, 3},
            {4, 5, 6},
            {7, 8, 9}
        };

        System.out.println("\nPrinting 2D array (matrix):");
        for (int i = 0; i < matrix.length; i++) {
            for (int j = 0; j < matrix[i].length; j++) {
                System.out.print(matrix[i][j] + " ");
            }
            System.out.println();
        }

        // Using enhanced for loops for nested iteration
        System.out.println("\nUsing enhanced for loops to iterate 2D array:");
        for (int[] row : matrix) {
            for (int element : row) {
                System.out.print(element + " ");
            }
            System.out.println();
        }

        // Combining different types of loops
        System.out.println("\nCombining for and while loops:");
        int i = 1;
        while (i <= 3) {
            System.out.println("Outer loop iteration: " + i);

            for (int j = 1; j <= 3; j++) {
                System.out.println("  Inner loop: " + j);
            }

            i++;
        }
    }
}

break/continue Statement.code:
public class BreakContinueExample {
    public static void main(String[] args) {
        // Using break in a for loop
        System.out.println("Using break in a for loop:");
        for (int i = 1; i <= 10; i++) {
            if (i == 6) {
                System.out.println("Breaking the loop at i = " + i);
                break;
            }
            System.out.println("i = " + i);
        }

        // Using continue in a for loop
        System.out.println("\nUsing continue in a for loop:");
        for (int i = 1; i <= 10; i++) {
            if (i % 2 == 0) {
                continue; // Skip even numbers
            }
            System.out.println("Odd number: " + i);
        }

        // Using break in a while loop
        System.out.println("\nUsing break in a while loop:");
        int count = 1;
        while (count <= 10) {
            System.out.println("Count: " + count);

            if (count == 5) {
                System.out.println("Breaking the while loop at count = " + count);
                break;
            }

            count++;
        }

        // Using continue in a while loop
        System.out.println("\nUsing continue in a while loop:");
        int i = 0;
        while (i < 10) {
            i++;

            if (i % 3 == 0) {
                continue; // Skip multiples of 3
            }

            System.out.println("i = " + i);
        }

        // Using break with nested loops
        System.out.println("\nUsing break in nested loops:");
        outerLoop: for (int x = 1; x <= 3; x++) {
            System.out.println("Outer loop iteration: " + x);

            for (int y = 1; y <= 3; y++) {
                System.out.println("  Inner loop iteration: " + y);

                if (x == 2 && y == 2) {
                    System.out.println("  Breaking both loops when x=2 and y=2");
                    break outerLoop; // Exit both loops
                }
            }
        }

        // Using break with labeled blocks
        System.out.println("\nUsing break with labeled block:");
        firstBlock: {
            System.out.println("Inside the first block");

            secondBlock: {
                System.out.println("Inside the second block");

                // Break out of the secondBlock
                if (true) {
                    System.out.println("Breaking out of secondBlock");
                    break secondBlock;
                }

                System.out.println("This line will not be executed");
            }

            System.out.println("Back to the first block");
        }
    }
}

String Basics.code:
public class StringBasicsExample {
    public static void main(String[] args) {
        // Creating strings
        String greeting = "Hello, World!";
        String emptyString = "";
        String name = new String("Java");  // Using constructor (not recommended)

        // String length
        System.out.println("Length of greeting: " + greeting.length());

        // Accessing characters in a string
        char firstChar = greeting.charAt(0);
        System.out.println("First character of greeting: " + firstChar);

        // String concatenation
        String firstName = "John";
        String lastName = "Doe";
        String fullName = firstName + " " + lastName;
        System.out.println("Full name: " + fullName);

        // String concatenation using concat() method
        String message = "Hello ".concat(firstName).concat("!");
        System.out.println("Message: " + message);

        // String comparison
        String str1 = "Hello";
        String str2 = "hello";
        String str3 = "Hello";

        // Using equals() method
        System.out.println("str1.equals(str2): " + str1.equals(str2));  // false (case-sensitive)
        System.out.println("str1.equals(str3): " + str1.equals(str3));  // true

        // Using equalsIgnoreCase() method
        System.out.println("str1.equalsIgnoreCase(str2): " + str1.equalsIgnoreCase(str2));  // true

        // Using == operator (compares reference, not content)
        String s1 = "Java";
        String s2 = "Java";
        String s3 = new String("Java");

        System.out.println("s1 == s2: " + (s1 == s2));  // true (same literal)
        System.out.println("s1 == s3: " + (s1 == s3));  // false (different objects)
        System.out.println("s1.equals(s3): " + s1.equals(s3));  // true (same content)

        // String compareTo() method
        System.out.println("apple".compareTo("banana"));  // negative (apple comes before banana)
        System.out.println("banana".compareTo("apple"));  // positive (banana comes after apple)
        System.out.println("apple".compareTo("apple"));   // zero (same string)

        // Checking if a string is empty
        System.out.println("Is emptyString empty? " + emptyString.isEmpty());

        // Checking if a string starts/ends with a prefix/suffix
        System.out.println("greeting starts with 'Hello': " + greeting.startsWith("Hello"));  // true
        System.out.println("greeting ends with 'World!': " + greeting.endsWith("World!"));  // true
    }
}

