String Methods.code:
public class StringMethodsExample {
    public static void main(String[] args) {
        String text = "  Java String Methods Example  ";

        // length() - returns the number of characters
        System.out.println("Length: " + text.length());

        // charAt() - returns the character at specified index
        System.out.println("Character at index 7: " + text.charAt(7));

        // substring() - returns part of the string
        System.out.println("Substring from index 7: " + text.substring(7));
        System.out.println("Substring from index 7 to 13: " + text.substring(7, 13));

        // indexOf() - returns index of first occurrence
        System.out.println("Index of 'String': " + text.indexOf("String"));
        System.out.println("Index of 'e': " + text.indexOf('e'));
        System.out.println("Index of 'e' after index 15: " + text.indexOf('e', 15));

        // lastIndexOf() - returns index of last occurrence
        System.out.println("Last index of 'e': " + text.lastIndexOf('e'));

        // toLowerCase() and toUpperCase()
        System.out.println("Lowercase: " + text.toLowerCase());
        System.out.println("Uppercase: " + text.toUpperCase());

        // trim() - removes whitespace from beginning and end
        System.out.println("Trimmed: '" + text.trim() + "'");

        // replace() - replaces characters
        System.out.println("Replace 'a' with 'o': " + text.replace('a', 'o'));

        // replaceAll() - replaces all occurrences matching the regex
        System.out.println("Replace all spaces: " + text.replaceAll("\\s", "-"));

        // contains() - checks if string contains a sequence
        System.out.println("Contains 'Methods': " + text.contains("Methods"));

        // startsWith() and endsWith()
        System.out.println("Starts with '  Java': " + text.startsWith("  Java"));
        System.out.println("Ends with 'Example  ': " + text.endsWith("Example  "));

        // isEmpty() - checks if string is empty
        System.out.println("Is empty: " + text.isEmpty());

        // String.join() - concatenates strings with a delimiter
        String joined = String.join(", ", "Java", "Python", "C++", "JavaScript");
        System.out.println("Joined string: " + joined);

        // split() - splits the string based on a delimiter
        String[] parts = joined.split(", ");
        System.out.println("Split result:");
        for (String part : parts) {
            System.out.println("  - " + part);
        }

        // valueOf() - converts different types to string
        String numStr = String.valueOf(123.45);
        System.out.println("Value of 123.45: " + numStr);

        // format() - returns a formatted string
        String formatted = String.format("Name: %s, Age: %d", "John", 25);
        System.out.println("Formatted string: " + formatted);

        // strip() method (Java 11+) - removes whitespace from both ends (similar to trim)
        System.out.println("Stripped: '" + text.strip() + "'");

        // repeat() method (Java 11+) - repeats the string
        String star = "*";
        System.out.println("Repeated: " + star.repeat(10));
    }
}

