public class ArrayBasicsExample {
    public static void main(String[] args) {
        // Declaration and creation
        int[] numbers = new int[5];

        // Initialization with values
        int[] scores = {95, 85, 75, 90, 88};

        // Accessing elements
        System.out.println(numbers[0]); // Access first element
        numbers[2] = 42; // Set third element to 42

        // Array length
        int length = numbers.length;

        // Iterating with standard for loop
        for (int i = 0; i < numbers.length; i++) {
            System.out.println("Element at " + i + ": " + numbers[i]);
        }

        // Enhanced for loop (for-each)
        for (int num : numbers) {
            System.out.println(num);
        }

        // Array Copying
        // Source array
        int[] source = {1, 2, 3, 4, 5};

        // Using System.arraycopy()
        int[] target1 = new int[source.length];
        System.arraycopy(source, 0, target1, 0, source.length);

        // Using Arrays.copyOf()
        import java.util.Arrays;
        int[] target2 = Arrays.copyOf(source, source.length);

        // Manual copy
        int[] target3 = new int[source.length];
        for (int i = 0; i < source.length; i++) {
            target3[i] = source[i];
        }

        // Array Utility Methods
        import java.util.Arrays;

        // Sorting an array
        int[] numArray = {5, 2, 9, 1, 8, 3};
        Arrays.sort(numArray);

        // Binary search (must be sorted first)
        int index = Arrays.binarySearch(numArray, 8);

        // Fill array with a value
        int[] newArray = new int[5];
        Arrays.fill(newArray, 10);

        // Compare arrays
        boolean areEqual = Arrays.equals(numArray, newArray);

        // Convert array to string
        String arrayString = Arrays.toString(numArray);
    }
}

