public class MethodBasicsExample {
    public static void main(String[] args) {
        // Method call
        greet();

        // Call method with parameters
        int sum = add(5, 3);
        System.out.println("Sum: " + sum);

        // Call method with return value directly
        System.out.println("Product: " + multiply(4, 7));

        // Variable Parameters Method
        // Call with different number of arguments
        printNumbers(1, 2, 3);
        printNumbers(10, 20, 30, 40, 50);
        printNumbers(); // No arguments is also valid

        // Call sum with different number of arguments
        System.out.println("Sum: " + sum(1, 2, 3, 4, 5));
        System.out.println("Sum: " + sum(10, 20));

        // Method with Object Parameters and Return Type
        // Create and use Student object
        Student student = createStudent("Alice", 20);
        System.out.println(student.getName() + " is " + student.getAge() + " years old");

        // Create list of students
        ArrayList<Student> students = createStudentList();

        // Process list of students
        double avgAge = calculateAverageAge(students);
        System.out.println("Average age: " + avgAge);
    }

    // Method with no parameters and no return value
    public static void greet() {
        System.out.println("Hello, World!");
    }

    // Method with parameters and return value
    public static int add(int a, int b) {
        return a + b;
    }

    // Another method with parameters and return value
    public static int multiply(int a, int b) {
        return a * b;
    }

    // Varargs method (variable number of arguments)
    public static void printNumbers(int... numbers) {
        System.out.println("Number of arguments: " + numbers.length);
        for (int num : numbers) {
            System.out.print(num + " ");
        }
        System.out.println();
    }

    // Varargs with return value
    public static int sum(int... numbers) {
        int total = 0;
        for (int num : numbers) {
            total += num;
        }
        return total;
    }

    // Method returning an object
    public static Student createStudent(String name, int age) {
        return new Student(name, age);
    }

    // Method returning a collection
    public static ArrayList<Student> createStudentList() {
        ArrayList<Student> students = new ArrayList<>();
        students.add(new Student("Bob", 22));
        students.add(new Student("Charlie", 19));
        students.add(new Student("David", 21));
        return students;
    }

    // Method taking collection parameter
    public static double calculateAverageAge(ArrayList<Student> students) {
        double sum = 0;
        for (Student student : students) {
            sum += student.getAge();
        }
        return students.isEmpty() ? 0 : sum / students.size();
    }

    // Inner class for Student
    static class Student {
        private String name;
        private int age;

        public Student(String name, int age) {
            this.name = name;
            this.age = age;
        }

        public String getName() { return name; }
        public int getAge() { return age; }
    }
}

Method Overloading.code:
public class MethodOverloadingExample {
    public static void main(String[] args) {
        // Call overloaded methods
        display(5);
        display("Hello");
        display(3.14);
        display(10, 20);
        display("Name", 25);

        // Overloaded Methods with Different Return Types
        // Different implementation of calculate method
        int sum = calculate(5, 3, "add");
        int product = calculate(5, 3, "multiply");
        double division = calculate(10.0, 2.0);

        System.out.println("Sum: " + sum);
        System.out.println("Product: " + product);
        System.out.println("Division: " + division);

        // Constructor Overloading Example
        // Create persons using different constructors
        Person p1 = new Person();
        Person p2 = new Person("John");
        Person p3 = new Person("Alice", 25);
        Person p4 = new Person("Bob", 30, "123 Main St");

        // Display information
        System.out.println("Person 1:");
        p1.displayInfo();

        System.out.println("\nPerson 2:");
        p2.displayInfo();

        System.out.println("\nPerson 3:");
        p3.displayInfo();

        System.out.println("\nPerson 4:");
        p4.displayInfo();
    }

    // Method with int parameter
    public static void display(int num) {
        System.out.println("Integer: " + num);
    }

    // Method with String parameter
    public static void display(String text) {
        System.out.println("String: " + text);
    }

    // Method with double parameter
    public static void display(double num) {
        System.out.println("Double: " + num);
    }

    // Method with two int parameters
    public static void display(int a, int b) {
        System.out.println("Two integers: " + a + " and " + b);
    }

    // Method with String and int parameters
    public static void display(String name, int age) {
        System.out.println("Name: " + name + ", Age: " + age);
    }

    // Method returning int with additional string parameter
    public static int calculate(int a, int b, String operation) {
        if (operation.equalsIgnoreCase("add")) {
            return a + b;
        } else if (operation.equalsIgnoreCase("multiply")) {
            return a * b;
        } else {
            return 0; // Default case
        }
    }

    // Method with double parameters returning double
    public static double calculate(double a, double b) {
        return a / b;
    }

    // Method with more parameters
    public static int calculate(int a, int b, int c) {
        return a + b + c;
    }

    // Person class for constructor overloading example
    static class Person {
        private String name;
        private int age;
        private String address;

        // Default constructor
        public Person() {
            this.name = "Unknown";
            this.age = 0;
            this.address = "Not specified";
        }

        // Constructor with name parameter
        public Person(String name) {
            this.name = name;
            this.age = 0;
            this.address = "Not specified";
        }

        // Constructor with name and age parameters
        public Person(String name, int age) {
            this.name = name;
            this.age = age;
            this.address = "Not specified";
        }

        // Constructor with all parameters
        public Person(String name, int age, String address) {
            this.name = name;
            this.age = age;
            this.address = address;
        }

        // Method to display person details
        public void displayInfo() {
            System.out.println("Name: " + name);
            System.out.println("Age: " + age);
            System.out.println("Address: " + address);
        }
    }
}

Recursive Functions.code:
public class RecursiveFunctionsExample {
    public static void main(String[] args) {
        // Factorial Example
        int n = 5;
        System.out.println(n + "! = " + factorial(n));

        // Calculate factorials for numbers 0 to 10
        for (int i = 0; i <= 10; i++) {
            System.out.println(i + "! = " + factorial(i));
        }

        // Fibonacci Example
        int fibN = 10;
        System.out.println("\nFibonacci sequence up to term " + fibN + ":");

        for (int i = 0; i <= fibN; i++) {
            System.out.print(fibonacci(i) + " ");
        }

        System.out.println("\n\nFibonacci term at position " + fibN + ": " + fibonacci(fibN));

        // Binary Search Example
        int[] sortedArray = {2, 5, 8, 12, 16, 23, 38, 45, 56, 72, 91};

        // Search for various values
        int[] valuesToSearch = {5, 23, 56, 100, 1};

        System.out.println("\nBinary Search Results:");
        for (int value : valuesToSearch) {
            int result = binarySearch(sortedArray, value, 0, sortedArray.length - 1);
            if (result == -1) {
                System.out.println(value + " not found in the array");
            } else {
                System.out.println(value + " found at index " + result);
            }
        }

        // Tree Traversal Example
        System.out.println("\nTree Traversal Example:");
        // Create a binary tree
        TreeNode root = new TreeNode(1);
        root.left = new TreeNode(2);
        root.right = new TreeNode(3);
        root.left.left = new TreeNode(4);
        root.left.right = new TreeNode(5);
        root.right.left = new TreeNode(6);
        root.right.right = new TreeNode(7);

        System.out.println("Inorder traversal:");
        inorderTraversal(root);
        System.out.println();

        System.out.println("Preorder traversal:");
        preorderTraversal(root);
        System.out.println();

        System.out.println("Postorder traversal:");
        postorderTraversal(root);
        System.out.println();
    }

    // Recursive factorial method
    public static long factorial(int n) {
        // Base case
        if (n == 0 || n == 1) {
            return 1;
        }

        // Recursive case
        return n * factorial(n - 1);
    }

    // Recursive Fibonacci method
    public static int fibonacci(int n) {
        // Base cases
        if (n <= 1) {
            return n;
        }

        // Recursive case: Fib(n) = Fib(n-1) + Fib(n-2)
        return fibonacci(n - 1) + fibonacci(n - 2);
    }

    // Recursive binary search method
    public static int binarySearch(int[] array, int target, int left, int right) {
        // Base case: element not found
        if (left > right) {
            return -1;
        }

        // Calculate middle point
        int mid = left + (right - left) / 2;

        // Check if target is present at mid
        if (array[mid] == target) {
            return mid;
        }

        // If target is smaller, search in left subarray
        if (array[mid] > target) {
            return binarySearch(array, target, left, mid - 1);
        }

        // If target is larger, search in right subarray
        return binarySearch(array, target, mid + 1, right);
    }

    // Tree node class for tree traversal example
    static class TreeNode {
        int value;
        TreeNode left;
        TreeNode right;

        TreeNode(int value) {
            this.value = value;
            left = null;
            right = null;
        }
    }

    // Recursive inorder traversal (Left, Root, Right)
    public static void inorderTraversal(TreeNode node) {
        if (node == null) {
            return;
        }

        inorderTraversal(node.left);  // Visit left subtree
        System.out.print(node.value + " ");  // Visit root
        inorderTraversal(node.right);  // Visit right subtree
    }

    // Recursive preorder traversal (Root, Left, Right)
    public static void preorderTraversal(TreeNode node) {
        if (node == null) {
            return;
        }

        System.out.print(node.value + " ");  // Visit root
        preorderTraversal(node.left);  // Visit left subtree
        preorderTraversal(node.right);  // Visit right subtree
    }

    // Recursive postorder traversal (Left, Right, Root)
    public static void postorderTraversal(TreeNode node) {
        if (node == null) {
            return;
        }

        postorderTraversal(node.left);  // Visit left subtree
        postorderTraversal(node.right);  // Visit right subtree
        System.out.print(node.value + " ");  // Visit root
    }
}

