    // Private fields (encapsulation)
    private String name;
    private int age;
    private double gpa;

    // Constructor
    public Student(String name, int age, double gpa) {
        this.name = name;
        this.age = age;
        this.gpa = gpa;
    }

    // Getters (accessor methods)
    public String getName() {
        return name;
    }

    public int getAge() {
        return age;
    }

    public double getGpa() {
        return gpa;
    }

    // Setters (mutator methods)
    public void setName(String name) {
        this.name = name;
    }

    public void setAge(int age) {
        if (age > 0) { // Validation
            this.age = age;
        }
    }

    public void setGpa(double gpa) {
        if (gpa >= 0.0 && gpa <= 4.0) { // Validation
            this.gpa = gpa;
        }
    }

    // Method to check if student has honors
    public boolean hasHonors() {
        return gpa >= 3.5;
    }
}

// Usage example
public class StudentDemo {
    public static void main(String[] args) {
        // Create Student objects
        Student student1 = new Student("John", 20, 3.8);
        Student student2 = new Student("Emma", 19, 3.2);

        // Access properties using getters
        System.out.println(student1.getName() + "'s GPA: " + student1.getGpa());
        System.out.println(student2.getName() + "'s GPA: " + student2.getGpa());

        // Check honors status
        System.out.println(student1.getName() + " has honors: " + student1.hasHonors());
        System.out.println(student2.getName() + " has honors: " + student2.hasHonors());

        // Modify properties using setters
        student2.setGpa(3.7);
        System.out.println("After GPA update:");
        System.out.println(student2.getName() + "'s GPA: " + student2.getGpa());
        System.out.println(student2.getName() + " has honors: " + student2.hasHonors());

        // Invalid value is ignored due to validation
        student1.setGpa(5.0); // Out of range
        System.out.println(student1.getName() + "'s GPA (unchanged): " + student1.getGpa());
    }
}"}, {"title": "Static Members (Class Variables and Methods)", "code": "public class MathUtils {
    // Static variable (class variable) - shared among all instances
    public static final double PI = 3.14159;
    private static int operationsPerformed = 0;

    // Instance variable - separate for each instance
    private String calculationName;

    public MathUtils(String calculationName) {
        this.calculationName = calculationName;
    }

    // Static method (class method)
    public static double calculateCircleArea(double radius) {
        operationsPerformed++;
        return PI * radius * radius;
    }

    // Static method with counter
    public static double calculateCircleCircumference(double radius) {
        operationsPerformed++;
        return 2 * PI * radius;
    }

    // Instance method (requires an instance)
    public void displayCalculation(double value) {
        System.out.println(calculationName + ": " + value);
    }

    // Static method to get operation count
    public static int getOperationsPerformed() {
        return operationsPerformed;
    }
}

// Usage example
public class StaticDemo {
    public static void main(String[] args) {
        // Access static variable directly from class
        System.out.println("PI value: " + MathUtils.PI);

        // Call static methods directly from class
        double area = MathUtils.calculateCircleArea(5);
        double circumference = MathUtils.calculateCircleCircumference(5);

        System.out.println("Circle area: " + area);
        System.out.println("Circle circumference: " + circumference);

        // Create instances for instance methods
        MathUtils calc1 = new MathUtils("Area calculation");
        MathUtils calc2 = new MathUtils("Circumference calculation");

        // Call instance methods
        calc1.displayCalculation(area);
        calc2.displayCalculation(circumference);

        // Access static counter
        System.out.println("Operations performed: " + MathUtils.getOperationsPerformed());

        // Perform more operations
        MathUtils.calculateCircleArea(10);
        MathUtils.calculateCircleCircumference(10);

        // Counter is shared among all instances
        System.out.println("Operations performed: " + MathUtils.getOperationsPerformed());
    }
}

