Inheritance.examples:
title": "Basic Inheritance", "code": "// Parent class
public class Animal {
    protected String name;
    protected int age;

    public Animal(String name, int age) {
        this.name = name;
        this.age = age;
    }

    public void eat() {
        System.out.println(name + " is eating");
    }

    public void sleep() {
        System.out.println(name + " is sleeping");
    }

    public String getName() {
        return name;
    }

    public int getAge() {
        return age;
    }
}

// Child class inheriting from Animal
public class Dog extends Animal {
    private String breed;

    public Dog(String name, int age, String breed) {
        super(name, age); // Call parent constructor
        this.breed = breed;
    }

    // New method specific to Dog
    public void bark() {
        System.out.println(name + " is barking");
    }

    // Override parent method
    @Override
    public void eat() {
        System.out.println(name + " the " + breed + " is eating dog food");
    }

    public String getBreed() {
        return breed;
    }
}

// Usage example
public class InheritanceDemo {
    public static void main(String[] args) {
        // Create Animal object
        Animal animal = new Animal("Generic Animal", 5);
        animal.eat();
        animal.sleep();

        // Create Dog object
        Dog dog = new Dog("Buddy", 3, "Golden Retriever");

        // Inherited methods
        dog.sleep(); // From Animal class

        // Overridden method
        dog.eat();   // Dog's implementation

        // Dog-specific method
        dog.bark();

        // Access properties
        System.out.println(dog.getName() + " is a " + dog.getBreed() + " and is " + 
                           dog.getAge() + " years old");
    }
}"}, {"title": "Multi-level Inheritance", "code": "// Base class
class Vehicle {
    protected String brand;
    protected int year;

    public Vehicle(String brand, int year) {
        this.brand = brand;
        this.year = year;
    }

    public void start() {
        System.out.println("Vehicle starting");
    }

    public void stop() {
        System.out.println("Vehicle stopping");
    }

    public String getInfo() {
        return brand + " " + year;
    }
}

// First level child class
class Car extends Vehicle {
    private int numDoors;

    public Car(String brand, int year, int numDoors) {
        super(brand, year);
        this.numDoors = numDoors;
    }

    @Override
    public void start() {
        System.out.println("Car engine starting");
    }

    public void drive() {
        System.out.println("Car driving");
    }

    @Override
    public String getInfo() {
        return super.getInfo() + ", " + numDoors + " doors";
    }
}

// Second level child class
class ElectricCar extends Car {
    private int batteryCapacity;

    public ElectricCar(String brand, int year, int numDoors, int batteryCapacity) {
        super(brand, year, numDoors);
        this.batteryCapacity = batteryCapacity;
    }

    @Override
    public void start() {
        System.out.println("Electric car powering up");
    }

    public void charge() {
        System.out.println("Electric car charging");
    }

    @Override
    public String getInfo() {
        return super.getInfo() + ", " + batteryCapacity + " kWh battery";
    }
}

// Usage example
public class MultilevelInheritanceDemo {
    public static void main(String[] args) {
        // Create objects of each class
        Vehicle vehicle = new Vehicle("Generic", 2020);
        Car car = new Car("Toyota", 2022, 4);
        ElectricCar electricCar = new ElectricCar("Tesla", 2023, 4, 75);

        // Call methods
        System.out.println("Vehicle Info: " + vehicle.getInfo());
        vehicle.start();
        vehicle.stop();

        System.out.println("\nCar Info: " + car.getInfo());
        car.start(); // Overridden method
        car.drive(); // Car-specific method
        car.stop();  // Inherited from Vehicle

        System.out.println("\nElectric Car Info: " + electricCar.getInfo());
        electricCar.start();  // Overridden method
        electricCar.drive();  // Inherited from Car
        electricCar.charge(); // ElectricCar-specific method
        electricCar.stop();   // Inherited from Vehicle
    }
}"}, {"title": "The 'super' Keyword", "code": "// Parent class
class Shape {
    protected String color;

    public Shape() {
        this.color = "transparent";
        System.out.println("Shape default constructor called");
    }

    public Shape(String color) {
        this.color = color;
        System.out.println("Shape parameterized constructor called");
    }

    public double calculateArea() {
        return 0.0; // Default implementation
    }

    public void display() {
        System.out.println("This is a shape with color: " + color);
    }
}

// Child class
class Rectangle extends Shape {
    private double length;
    private double width;

    public Rectangle() {
        super(); // Call parent default constructor
        this.length = 1.0;
        this.width = 1.0;
        System.out.println("Rectangle default constructor called");
    }

    public Rectangle(double length, double width) {
        this(length, width, "black"); // Call another constructor in this class
        System.out.println("Rectangle 2-param constructor called");
    }

    public Rectangle(double length, double width, String color) {
        super(color); // Call parent parameterized constructor
        this.length = length;
        this.width = width;
        System.out.println("Rectangle 3-param constructor called");
    }

    @Override
    public double calculateArea() {
        return length * width;
    }

    @Override
    public void display() {
        super.display(); // Call parent method
        System.out.println("This is a rectangle with length: " + length + 
                         " and width: " + width);
        System.out.println("Area: " + calculateArea());
    }
}

// Usage
public class SuperKeywordDemo {
    public static void main(String[] args) {
        System.out.println("Creating a default rectangle:");
        Rectangle rect1 = new Rectangle();
        rect1.display();

        System.out.println("\nCreating a rectangle with dimensions:");
        Rectangle rect2 = new Rectangle(5.0, 3.0);
        rect2.display();

        System.out.println("\nCreating a rectangle with dimensions and color:");
        Rectangle rect3 = new Rectangle(4.0, 2.0, "blue");
        rect3.display();
    }
}

