// Usage example
public class PolymorphismDemo {
    public static void main(String[] args) {
        // Create Animal, Dog and Cat objects
        Animal animal = new Animal();
        Animal dog = new Dog();     // Upcasting - a Dog is treated as an Animal
        Animal cat = new Cat();     // Upcasting - a Cat is treated as an Animal

        // Call methods on the Animal reference
        System.out.println("Using Animal reference:");
        animal.makeSound();
        animal.eat();

        // Call methods on the Dog reference (polymorphic behavior)
        System.out.println("\nUsing Dog reference (polymorphic):");
        dog.makeSound();  // Calls Dog's implementation
        dog.eat();        // Calls Dog's implementation
        // dog.wagTail(); // Error: not accessible through Animal reference

        // Call methods on the Cat reference (polymorphic behavior)
        System.out.println("\nUsing Cat reference (polymorphic):");
        cat.makeSound();  // Calls Cat's implementation
        cat.eat();        // Calls Cat's implementation
        // cat.purr();    // Error: not accessible through Animal reference

        // Demonstrate downcasting
        System.out.println("\nDemonstrating downcasting:");
        if (dog instanceof Dog) {
            Dog actualDog = (Dog) dog;  // Downcasting
            actualDog.wagTail();        // Now we can call Dog-specific method
        }

        if (cat instanceof Cat) {
            Cat actualCat = (Cat) cat;  // Downcasting
            actualCat.purr();           // Now we can call Cat-specific method
        }

        // Polymorphic method call
        System.out.println("\nPolymorphic method calls:");
        processAnimal(animal);
        processAnimal(dog);
        processAnimal(cat);
    }

    // Method that takes an Animal parameter but processes different subclasses differently
    public static void processAnimal(Animal animal) {
        System.out.println("Processing animal:");
        animal.makeSound(); // Polymorphic call - behavior depends on actual object type
    }
}"}, {"title": "Interface Polymorphism", "code": "// Interface definition
interface Drawable {
    void draw(); // Abstract method (no implementation)

    // Default method (with implementation) - Java 8 and later
    default void displayInfo() {
        System.out.println("This is a drawable object");
    }
}

// Classes implementing the interface
class Circle implements Drawable {
    private double radius;

    public Circle(double radius) {
        this.radius = radius;
    }

    @Override
    public void draw() {
        System.out.println("Drawing a circle with radius " + radius);
    }

    @Override
    public void displayInfo() {
        System.out.println("This is a circle with radius " + radius);
    }
}

class Rectangle implements Drawable {
    private double length;
    private double width;

    public Rectangle(double length, double width) {
        this.length = length;
        this.width = width;
    }

    @Override
    public void draw() {
        System.out.println("Drawing a rectangle with length " + length + 
                          " and width " + width);
    }

    // Using default implementation from interface
}

class Triangle implements Drawable {
    private double base;
    private double height;

    public Triangle(double base, double height) {
        this.base = base;
        this.height = height;
    }

    @Override
    public void draw() {
        System.out.println("Drawing a triangle with base " + base + 
                          " and height " + height);
    }
}

// Usage example
public class InterfacePolymorphismDemo {
    public static void main(String[] args) {
        // Create objects
        Drawable circle = new Circle(5.0);
        Drawable rectangle = new Rectangle(4.0, 6.0);
        Drawable triangle = new Triangle(3.0, 4.0);

        // Call methods polymorphically
        drawShape(circle);
        drawShape(rectangle);
        drawShape(triangle);

        // Show info
        System.out.println("\nDisplaying information:");
        circle.displayInfo();    // Overridden method
        rectangle.displayInfo(); // Default method from interface
        triangle.displayInfo();  // Default method from interface
    }

    // Polymorphic method that works with any Drawable object
    public static void drawShape(Drawable drawable) {
        drawable.draw();
    }
}"}, {"title": "Abstract Class Polymorphism", "code": "// Abstract class
abstract class Shape {
    protected String color;

    // Constructor
    public Shape(String color) {
        this.color = color;
    }

    // Abstract method - must be implemented by subclasses
    public abstract double calculateArea();

    // Concrete method - inherited by all subclasses
    public void display() {
        System.out.println("This is a " + color + " shape");
    }

    // Getter method
    public String getColor() {
        return color;
    }
}

// Concrete subclasses
class Circle extends Shape {
    private double radius;

    public Circle(String color, double radius) {
        super(color);
        this.radius = radius;
    }

    @Override
    public double calculateArea() {
        return Math.PI * radius * radius;
    }

    @Override
    public void display() {
        System.out.println("This is a " + color + " circle with radius " + radius);
        System.out.println("Area: " + calculateArea());
    }
}

class Rectangle extends Shape {
    private double length;
    private double width;

    public Rectangle(String color, double length, double width) {
        super(color);
        this.length = length;
        this.width = width;
    }

    @Override
    public double calculateArea() {
        return length * width;
    }

    @Override
    public void display() {
        System.out.println("This is a " + color + " rectangle with length " + 
                         length + " and width " + width);
        System.out.println("Area: " + calculateArea());
    }
}

// Usage example
public class AbstractPolymorphismDemo {
    public static void main(String[] args) {
        // Shape shape = new Shape("red"); // Error: Cannot instantiate abstract class

        // Create objects using polymorphic references
        Shape circle = new Circle("blue", 5.0);
        Shape rectangle = new Rectangle("green", 4.0, 6.0);

        // Process shapes polymorphically
        System.out.println("Processing shapes:");
        processShape(circle);
        processShape(rectangle);

        // Array of shapes - demonstrating polymorphism
        Shape[] shapes = new Shape[3];
        shapes[0] = new Circle("red", 3.0);
        shapes[1] = new Rectangle("yellow", 2.0, 8.0);
        shapes[2] = new Circle("purple", 7.0);

        System.out.println("\nProcessing shapes from array:");
        for (Shape shape : shapes) {
            processShape(shape);
        }
    }

    // Polymorphic method that works with any Shape subclass
    public static void processShape(Shape shape) {
        System.out.println("Shape color: " + shape.getColor());
        System.out.println("Shape area: " + shape.calculateArea());
        shape.display(); // Polymorphic call
        System.out.println();
    }
}"}]

Abstraction.examples:
{"AbstractClasses":{"title":"Abstract Classes","code":"// Abstract class
abstract class DatabaseConnection {
    protected String connectionString;
    protected boolean isConnected;

    // Constructor
    public DatabaseConnection(String connectionString) {
        this.connectionString = connectionString;
        this.isConnected = false;
    }

    // Abstract methods - must be implemented by subclasses
    abstract void connect();
    abstract void disconnect();
    abstract void executeQuery(String query);

    // Concrete methods shared by all subclasses
    public boolean isConnected() {
        return isConnected;
    }

    public void showConnectionInfo() {
        System.out.println("Connection string: " + connectionString);
        System.out.println("Connected: " + isConnected);
    }
}

// Concrete subclass implementing the abstract class
class MySQLConnection extends DatabaseConnection {
    private String username;
    private String password;

    public MySQLConnection(String host, String database, String username, String password) {
        super("jdbc:mysql://" + host + "/" + database);
        this.username = username;
        this.password = password;
    }

    @Override
    void connect() {
        // In a real application, this would contain actual connection code
        System.out.println("Connecting to MySQL database using " + connectionString);
        System.out.println("Username: " + username);
        isConnected = true;
    }

    @Override
    void disconnect() {
        System.out.println("Disconnecting from MySQL database");
        isConnected = false;
    }

    @Override
    void executeQuery(String query) {
        if (isConnected) {
            System.out.println("Executing MySQL query: " + query);
        } else {
            System.out.println("Cannot execute query. Not connected to database.");
        }
    }
}

// Another concrete subclass
class PostgreSQLConnection extends DatabaseConnection {
    private String username;
    private String password;

    public PostgreSQLConnection(String host, String database, String username, String password) {
        super("jdbc:postgresql://" + host + "/" + database);
        this.username = username;
        this.password = password;
    }

    @Override
    void connect() {
        System.out.println("Connecting to PostgreSQL database using " + connectionString);
        System.out.println("Username: " + username);
        isConnected = true;
    }

    @Override
    void disconnect() {
        System.out.println("Disconnecting from PostgreSQL database");
        isConnected = false;
    }

    @Override
    void executeQuery(String query) {
        if (isConnected) {
            System.out.println("Executing PostgreSQL query: " + query);
        } else {
            System.out.println("Cannot execute query. Not connected to database.");
        }
    }
}

// Usage example
public class AbstractionDemo {
    public static void main(String[] args) {
        // Create database connections
        DatabaseConnection mysql = new MySQLConnection("localhost", "mydb", "user", "password");
        DatabaseConnection postgres = new PostgreSQLConnection("localhost", "pgdb", "admin", "secure123");

        // Work with MySQL connection
        System.out.println("=== MySQL Connection ===\n");
        mysql.connect();
        mysql.showConnectionInfo();
        mysql.executeQuery("SELECT * FROM users");
        mysql.disconnect();

        System.out.println("\n=== PostgreSQL Connection ===\n");
