Encapsulation.examples:
{"BasicEncapsulationWithPrivateFields":{"title":"Basic Encapsulation with Private Fields","code":"public class BankAccount {
    // Private fields - not accessible from outside the class
    private String accountNumber;
    private String ownerName;
    private double balance;
    private boolean isLocked;

    // Constructor
    public BankAccount(String accountNumber, String ownerName) {
        this.accountNumber = accountNumber;
        this.ownerName = ownerName;
        this.balance = 0.0;
        this.isLocked = false;
    }

    // Getter methods
    public String getAccountNumber() {
        return accountNumber;
    }

    public String getOwnerName() {
        return ownerName;
    }

    public double getBalance() {
        return balance;
    }

    public boolean isLocked() {
        return isLocked;
    }

    // Setter method with validation
    public void setOwnerName(String ownerName) {
        if (ownerName != null && !ownerName.trim().isEmpty()) {
            this.ownerName = ownerName;
        }
    }

    // No setter for accountNumber (immutable)
    // No direct setter for balance (controlled through deposit/withdraw)

    // Business logic methods
    public void deposit(double amount) {
        if (isLocked) {
            System.out.println("Transaction failed. Account is locked.");
            return;
        }

        if (amount > 0) {
            this.balance += amount;
            System.out.println("Deposited: $" + amount);
            System.out.println("New balance: $" + this.balance);
        } else {
            System.out.println("Invalid deposit amount.");
        }
    }

    public void withdraw(double amount) {
        if (isLocked) {
            System.out.println("Transaction failed. Account is locked.");
            return;
        }

        if (amount > 0 && amount <= balance) {
            this.balance -= amount;
            System.out.println("Withdrawn: $" + amount);
            System.out.println("Remaining balance: $" + this.balance);
        } else {
            System.out.println("Invalid withdrawal amount or insufficient funds.");
        }
    }

    public void lockAccount() {
        this.isLocked = true;
        System.out.println("Account locked successfully.");
    }

    public void unlockAccount() {
        this.isLocked = false;
        System.out.println("Account unlocked successfully.");
    }
}"}}

