import java.nio.file.Path;
import java.nio.file.Paths;
import java.nio.file.StandardOpenOption;
import java.io.IOException;
import java.util.List;
import java.util.Arrays;

public class NioReadWriteExample {
    public static void main(String[] args) {
        Path path = Paths.get("nio_text.txt");

        // Writing to a file
        try {
            List<String> lines = Arrays.asList(
                "Line 1 - NIO example",
                "Line 2 - NIO example",
                "Line 3 - NIO example"
            );
            Files.write(path, lines);
            System.out.println("Data written to file.");

            // Append to file
            Files.write(
                path, 
                Arrays.asList("Line 4 - Appended", "Line 5 - Appended"),
                StandardOpenOption.APPEND
            );

            // Reading all lines from a file
            List<String> readLines = Files.readAllLines(path);
            System.out.println("\nReading from file:");
            for (String line : readLines) {
                System.out.println(line);
            }

            // Reading all bytes and converting to string
            byte[] bytes = Files.readAllBytes(path);
            String content = new String(bytes);
            System.out.println("\nFile content as a single string:");
            System.out.println(content);
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}"}}

