{"example1":{"title":"Basic Regex Pattern Matching","code":"import java.util.regex.Matcher;
import java.util.regex.Pattern;

public class RegexBasicExample {
    public static void main(String[] args) {
        // Simple pattern matching
        Pattern pattern = Pattern.compile("hello");
        Matcher matcher = pattern.matcher("hello world");

        boolean matchFound = matcher.find();
        System.out.println("Match found: " + matchFound);

        // Get the matched substring
        if (matchFound) {
            System.out.println("Match: " + matcher.group());
            System.out.println("Start index: " + matcher.start());
            System.out.println("End index: " + matcher.end());
        }

        // Case insensitive matching
        Pattern patternCI = Pattern.compile("hello", Pattern.CASE_INSENSITIVE);
        Matcher matcherCI = patternCI.matcher("Hello World");
        System.out.println("Case insensitive match: " + matcherCI.find());

        // Check if entire string matches pattern
        System.out.println("Full match: " + Pattern.matches("hello", "hello"));
        System.out.println("Full match: " + Pattern.matches("hello", "hello world"));
    }
}"},"example2":{"title":"Regex Character Classes and Quantifiers","code":"import java.util.regex.Matcher;
import java.util.regex.Pattern;

public class RegexPatternsExample {
    public static void main(String[] args) {
        // Character classes
        testPattern("[0-9]+", "I have 123 apples and 456 oranges"); // Match one or more digits
        testPattern("[a-zA-Z]+", "Hello123World"); // Match one or more letters
        testPattern("[^0-9]", "1a2b3c"); // Match anything that's not a digit

        // Predefined character classes
        testPattern("\\d+", "Phone: 555-123-4567"); // \d matches digits
        testPattern("\\w+", "Hello_123"); // \w matches word characters (letters, digits, underscore)
        testPattern("\\s+", "Hello   World"); // \s matches whitespace

        // Quantifiers
        testPattern("a*", "aaabbaaa"); // * means 0 or more
        testPattern("a+", "aaabbaaa"); // + means 1 or more
        testPattern("a?", "aaabbaaa"); // ? means 0 or 1
        testPattern("a{2}", "aaabbaaa"); // {n} means exactly n
        testPattern("a{2,4}", "aaabbaaa"); // {n,m} means between n and m

        // Email validation example
        String emailRegex = "^[a-zA-Z0-9_+&*-]+(?:\\.[a-zA-Z0-9_+&*-]+)*@(?:[a-zA-Z0-9-]+\\.)+[a-zA-Z]{2,7}$";
        testPattern(emailRegex, "user@example.com");
        testPattern(emailRegex, "invalid.email@com");
    }

    public static void testPattern(String regex, String input) {
        Pattern pattern = Pattern.compile(regex);
        Matcher matcher = pattern.matcher(input);

        System.out.println("\nPattern: " + regex);
        System.out.println("Input: " + input);

        boolean found = false;
        while (matcher.find()) {
            System.out.println("Found match: '" + matcher.group() + "' at position " + 
                             matcher.start() + "-" + (matcher.end() - 1));
            found = true;
        }

        if (!found) {
            System.out.println("No matches found.");
        }
    }
}"},"example3":{"title":"String Operations with Regex","code":"import java.util.Arrays;

public class RegexStringOperationsExample {
    public static void main(String[] args) {
        String input = "The quick brown fox jumps over the lazy dog.";

        // Split string using regex
        String[] words = input.split("\\s+"); // Split by one or more whitespace characters
        System.out.println("Split result: " + Arrays.toString(words));

        // Replace using regex
        String replaced = input.replaceAll("[aeiou]", "*"); // Replace all vowels with asterisks
        System.out.println("After replacing vowels: " + replaced);

        // Replace first occurrence only
        String replacedFirst = input.replaceFirst("[aeiou]", "*");
        System.out.println("After replacing first vowel: " + replacedFirst);

        // Check if string matches pattern
        boolean matches = input.matches(".*fox.*"); // Contains the word "fox"
        System.out.println("Contains 'fox': " + matches);

        // Extract numbers from text
        String text = "The price is $25.99 and the quantity is 3";
        String[] numbers = text.split("[^0-9.]+");
        System.out.println("Extracted numbers: " + Arrays.toString(numbers));

        // Format phone number
        String phoneNumber = "5551234567";
        String formatted = phoneNumber.replaceFirst("(\\d{3})(\\d{3})(\\d{4})", "($1) $2-$3");
        System.out.println("Formatted phone number: " + formatted);
    }
}"},"example4":{"title":"Regex Groups and Backreferences","code":"import java.util.regex.Matcher;
import java.util.regex.Pattern;

public class RegexGroupsExample {
    public static void main(String[] args) {
        // Using capturing groups
        Pattern pattern = Pattern.compile("(\\d{3})-(\\d{3})-(\\d{4})");
        Matcher matcher = pattern.matcher("My phone number is 555-123-4567.");

        if (matcher.find()) {
            System.out.println("Full match: " + matcher.group(0)); // The entire match
            System.out.println("Group 1: " + matcher.group(1)); // First capture group (area code)
            System.out.println("Group 2: " + matcher.group(2)); // Second capture group (prefix)
            System.out.println("Group 3: " + matcher.group(3)); // Third capture group (line number)
        }

        // Named capturing groups (Java 7+)
        Pattern namedPattern = Pattern.compile("(?<areaCode>\\d{3})-(?<prefix>\\d{3})-(?<lineNum>\\d{4})");
        Matcher namedMatcher = namedPattern.matcher("Contact: 800-555-1212");

        if (namedMatcher.find()) {
            System.out.println("\nArea Code: " + namedMatcher.group("areaCode"));
            System.out.println("Prefix: " + namedMatcher.group("prefix"));
            System.out.println("Line Number: " + namedMatcher.group("lineNum"));
        }

        // Backreferences - find repeated words
        Pattern repeatPattern = Pattern.compile("\\b(\\w+)\\s+\\1\\b");
        Matcher repeatMatcher = repeatPattern.matcher("The the quick brown fox jumps over the lazy dog dog.");

        System.out.println("\nRepeated words:");
        while (repeatMatcher.find()) {
            System.out.println("Repeated word: " + repeatMatcher.group(1) + 
                             " at position " + repeatMatcher.start());
        }

        // Backreferences in replacement
        String htmlText = "<h1>Title</h1><p>Paragraph</p>";
        // Swap tag contents
        String swapped = htmlText.replaceAll("<([^>]*)>([^<]*)</\\1>", "<$1>REPLACED: $2</$1>");
        System.out.println("\nOriginal HTML: " + htmlText);
        System.out.println("Modified HTML: " + swapped);
    }
}"}}
